/*DATOS PERSONALES VÍCTIMAS DIRECTAS*/

SELECT
folio_si,
calificacion,
región_comisión,
COUNT(DISTINCT idsibenef) as total_víctimas,
GROUP_CONCAT(CONCAT_WS(",",tipo_vic)) as tipo_víctimas,
GROUP_CONCAT(CONCAT_WS(",",sexo_vic)) as sexo_víctimas,
GROUP_CONCAT(CONCAT_WS(",",edad_vic)) as edad_víctimas,
descripcion_hechos,
municipio AS municipio_expediente,
entidad,
fecha_llenado,
fecha_conclusion,
estado_queja,
causal_conclusión
-- SUBSTR(fecha_llenado, 1, 4) AS año_llenado,
-- fecha_hechos,
-- SUBSTR(fecha_hechos, 1, 4) AS año_hechos,
-- idsi,
-- idsiant,
-- conoce_via,
-- GROUP_CONCAT(CONCAT_WS(",",nombre_pet)) as nombres_peticionarios,
-- GROUP_CONCAT(CONCAT_WS(",",identidadgenero_vic)) as identidadgenero_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",escolaridad_vic)) as escolaridad_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",municipio_vic)) as municipio_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",entidad_vic)) as entidad_víctimas,
-- lugar_insidente,
-- GROUP_CONCAT(CONCAT_WS(",",nacionalidad_vic)) as nacionalidad_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",tipo_vic)) as tipo_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",etnia_vic)) as etnia_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",idioma_vic)) as idioma_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",clasificación_edad)) as clasificación_edad_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",escolaridad_vic)) as escolaridad_víctimas,
-- GROUP_CONCAT(CONCAT_WS(",",ocupacion_vic)) as ocupacion_víctimas */
-- idclave_concluido,
FROM
(SELECT
IF(si.idclave_concluido IS NULL OR idclave_concluido = 0, 'SIN REGISTRO', IF(idclave_concluido = 1, 'ACTIVO', 'CONCLUIDO')) AS estado_queja,
IF(si.idclave_concluido IS NULL OR idclave_concluido = 0, 'SIN REGISTRO', IF(si.idclave_concluido = 1, 'ACTIVO', catcon.motivo_conclusion)) AS causal_conclusión,
peti.edad,
catsex.sexo,
peti.idsibenef,
catcal.calificacion,
si.fecha_llenado,
si.fecha_hechos,
IF(si.fecha_conclusion IS NULL, 'NA', si.fecha_conclusion) AS fecha_conclusion,
si.idsi, 
si.idsiant,
catmun.municipio,
si.lugar_insidente,
catent.entidad,
si.idclave_concluido,
si.descripcion_hechos,
si.folio_si, 
catvia.conoce_via,
CASE
        WHEN SUBSTR(si.folio_si, 1, 3) = 'ACA' THEN 'DELEGACIÓN ACAYUCAN'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'COA' THEN 'DELEGACIÓN COATZACOALCOS'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'COR' THEN 'DELEGACIÓN CÓRDOBA'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'CHI' THEN 'DELEGACIÓN CHICONTEPEC'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'PAP' THEN 'DELEGACIÓN PAPANTLA'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'TUX' THEN 'DELEGACIÓN TUXPAN'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'VER' THEN 'DELEGACIÓN VERACRUZ'
        WHEN SUBSTR(si.folio_si, 1, 3) = 'ZON' THEN 'DELEGACIÓN ZONGOLICA'
        ELSE 'OFICINAS CENTRALES XALAPA'
        END AS región_comisión,
if(peti.edad >= 0 AND peti.edad IS NOT NULL, peti.edad, 'NO PROPORCIONÓ') as edad_vic, 
if(peti.idsexo = 0 OR peti.idsexo = 4 OR peti.idsexo IS NULL, 'NO PROPORCIONÓ', catsex.sexo) as sexo_vic,
if(peti.idnacionalidad <= 102 AND peti.idnacionalidad <> 0, catnan.nacionalidad, 'NO PROPORCIONÓ') as nacionalidad_vic,
IF(peti.edad > 0 AND peti.edad <= 11, 'Niñas', IF(peti.edad >= 12 AND peti.edad <= 17, 'Adolescentes', IF(peti.edad >= 18, 'Mujeres', 'NO PROPORCIONÓ'))) as clasificación_edad,
IF(peti.idtipopet = 1, 'VÍCTIMA DIRECTA', IF(peti.idtipopet = 2, 'VÍCTIMA INDIRECTA', IF(peti.idtipopet = 3, 'PETICIONARIO GESTORÍA/ORIENTACIÓN', 'NO PROPORCIONÓ'))) as tipo_vic,
if(peti.idg = 0 OR peti.idg IS NULL, 'NO PROPORCIONÓ', idgen.identidadgenero) as identidadgenero_vic,
if(peti.idetnia >= 1 AND peti.idetnia <= 21, catetn.etnia, 'NO PROPORCIONÓ') as etnia_vic,
if(peti.ididioma > 0, catidi.idioma, 'SIN REGISTRO') as idioma_vic,
IF(LENGTH(peti.instruccion) <> 0, peti.instruccion, 'NO PROPORCIONÓ') AS escolaridad_vic,
IF(LENGTH(peti.ocupacion) <> 0, peti.ocupacion, 'NO PROPORCIONÓ') as ocupacion_vic,
if(catmunPet.municipio IS NOT NULL, catmunPet.municipio, 'NO PROPORCIONÓ') as municipio_vic,
if(catentPet.entidad IS NOT NULL, catentPet.entidad, 'NO PROPORCIONÓ') as entidad_vic,
CONCAT_WS(' ', TRIM(peti.nombre), TRIM(peti.ap_paterno), TRIM(peti.ap_materno)) as nombre_pet
FROM gestioncedhver.e_si as si
LEFT JOIN gestioncedhver.e_cat_calificacion AS catcal ON catcal.idcal = si.idcal
LEFT JOIN gestioncedhver.e_si_peticionario AS peti ON peti.idsi = si.idsi
LEFT JOIN gestioncedhver.e_cat_sexo AS catsex ON catsex.idsexo = peti.idsexo
LEFT JOIN gestioncedhver.cat_identidad_genero AS idgen ON idgen.ididenttidadgenero = peti.idg
LEFT JOIN gestioncedhver.e_cat_nacionalidad AS catnan ON catnan.idnacionalidad = peti.idnacionalidad
LEFT JOIN gestioncedhver.e_cat_etnia AS catetn ON catetn.idetnia = peti.idetnia
LEFT JOIN gestioncedhver.e_cat_idioma AS catidi ON catidi.ididio = peti.ididioma
LEFT JOIN gestioncedhver.e_cat_cve_conclusiones AS catcon ON catcon.idcveco = si.idclave_concluido
LEFT JOIN gestioncedhver.cat_municipios AS catmun ON catmun.idmunicipio = si.idmunicipio
LEFT JOIN gestioncedhver.cat_entidades AS catent ON catent.identidad = catmun.identidad
LEFT JOIN gestioncedhver.e_cat_conoce_via AS catvia ON catvia.idconvia=si.idconocevia
