/*DATOS PERSONALES REPRESENTANTES*/

SELECT
folio_si,
calificacion,
COUNT(DISTINCT idsirep) as total_representantes,
GROUP_CONCAT(CONCAT_WS(",",personas)) as nombres_representantes,
GROUP_CONCAT(CONCAT_WS(",",sexo_rep)) as sexo_representantes
FROM
(SELECT 
si.folio_si, 
si.fecha_llenado, 
catcal.calificacion, CONCAT_WS(' ', TRIM(nombre_rep), TRIM(ap_paterno_rep), TRIM(ap_materno_rep)) as personas,
